INSERT INTO `about_us` (`id`, `img1`, `a1`, `a2`, `a3`, `a4`, `a5`, `a6`, `a7`, `a8`, `a9`, `a10`, `a11`, `a12`, `a13`, `img3`, `img4`, `img5`) VALUES
(1, 'https://cmkt-image-prd.global.ssl.fastly.net/0.1.0/ps/106399/910/606/m1/fpnw/wm1/crsrxfllostehmuxwzqfkdarpcg0di40toehyl4mzmgrkmy3dpfzxttukvsmluvp-.jpg?1399222059&s=4cac59f118bf8790870fa5cf190d9131', 'Pull up a chair. Take a taste. Come join us. Life is so endlessly delicious.', 'COOKMANIA is a best platform for FOODIES. COOKMANIA provides you some amazing recipes from the worlds top cuisines.', 'ITS TASTE THAT TRANSFORMS THE ORDINARY INTO THE EXTRAORDINARY', 'MEET THE FOUNDERS', 'logo/meetTheFounders.jpg', 'MEET THE', 'FOUNDERS', 'M.Abdullah Kazmi', 'He did his matric from Divisional Public School, his FSc from Forman Christain College and currently studying Computer Science from National University of Sciences and Technology. He is currently working as an events executive in Computer Society of Pakistan and Association for Computer Machinery.', 'Raeed Asif', 'He did his O-levels and A-levels from Beacon House and is currently studying Computer Science from National University of Sciences and Technology. Photography is his passion. He is currently working in Student Government Association at the post of Director Media.', 'Zaid bin Arif', 'According to his facebook profile, he lives in Faisalabad and is also from Faisalabad.', 'logo/MAbdullahKazmi.jpg', 'logo/RaeedAsif.jpg', 'logo/ZaidBinArif.jpg');

